import React from 'react'
import styled from 'styled-components'

const Project = props  => {


    return (
        <CardContainer>
            <h1>Project Title</h1>
            <h2>A subscription based model for millennials to rent maruti cars.</h2>
            <ProjectContainer>
            <img src={props.image} alt='dfgdfg' />
            <h3>{props.title}</h3>
            <p>{props.text}</p>
            </ProjectContainer>
            <p>CASE STUDY 02.03</p>
        </CardContainer>
    )
}

export default Project

const ProjectContainer = styled.div`
    margin-bottom: 100px;
    width: 1000px;
    height: 500px;
    position: relative;
    overflow: hidden;
    border-radius: 20px;
    box-shadow: 0 20px 40px black;
    display: grid;
    grid-template-rows: 1fr 1fr;
    transition: 0.8s cubic-bezier(0.2, 0.2, 0.165, 1);


    :hover { 
        transform: scale(1.1, 1.1);
        box-shadow: 0 30px 60px rgba(0,0,0, 0.5);
    }

    img {
        position: absolute;
        top: 0;
        height: 100%;
        width: 1000px;
        z-index: 1;
        transition: 0.8s cubic-bezier(0.2, 0.2, 0.165, 1);

        :hover {
            transform: translateY(-10px) scale(1.1, 1.1);;
        } 
    }

    h3 {
        color: white;
        font-size: 30px;
        margin: 20px 0 0 20px;
        width: 190px;
        z-index: 1;
    }

    p {
        color: white;
        font-size: 15px;
        margin: 20px 0 0 20px;
        width: 190px;
        z-index: 1;
        text-transform: uppercase;
        align-self: end;
        margin: 0 0 20px 20px;
    }
`

const CardContainer = styled.div`
    border-bottom: 1px solid rgba(0,0,0, 0.1);

`
