import React from 'react'
import styled from 'styled-components'
import Hero from '../components/Hero'
import Header from '../components/Header'
import ExtraCard from '../components/ExtraCard'
import Footer from '../components/Footer'
import MobileHeader from '../components/MobileHeader'
import ViewProject from '../components/ViewProject'
import products from '../products'

import { AnimatePresence } from "framer-motion";


const Index = ({ }) => {



    return (
        <AnimatePresence>
            <Header />
            <MobileHeader />
            <Hero />
            <CardGroup>

            <SectionCaption>Let's Connect</SectionCaption>

               {products.map(product => (
                   <ViewProject product={product} />
               ))}


            </CardGroup>
            <Group>
                <h1>React JS Projects</h1>
                <ExtraCard
                    text='github project' 
                    // title='React for Designers'
                    image='/images/ecomm_mobile.png' 
                />
                <ExtraCard
                    // text='12 sections' 
                    // title='React for Designers'
                    image='/images/darcys-ss.png' 
                />
                <ExtraCard
                    // text='12 sections' 
                    // title='React for Designers'
                    image='/images/tge-ss.png' 
                />
                <ExtraCard
                    // text='12 sections' 
                    // title='React for Designers'
                    image='/images/project_image_blog.png' 
                />
                <ExtraCard
                    // text='12 sections' 
                    // title='React for Designers'
                    image='/images/re-ss.png' 
                />
                <ExtraCard
                    // text='12 sections' 
                    // title='React for Designers'
                    image='/images/nba-ss.png' 
                />
                <ExtraCard
                    // text='12 sections' 
                    // title='React for Designers'
                    image='/images/to-do-mobile-ss.png' 
                />
                <ExtraCard
                    // text='12 sections' 
                    // title='React for Designers'
                    image='/images/darcys-ss.png' 
                />
            </Group>
            <Footer />

        </AnimatePresence>
    )
}

export default Index

const CardGroup = styled.div`
    margin: 50px 40px 100px;
    padding: 150px;
    display: grid;
    grid-template-columns: repeat(1, 1fr);
    grid-gap: 40px;
    justify-items: center;


        @media (max-width: 1060px) {
            grid-template-columns: repeat(2, 1fr);
            padding: 20px;
        }
        @media (max-width: 720px) {
            grid-template-columns: repeat(1, 1fr);
        }
`

const Group = styled.div`
margin: 50px 40px 100px;
padding: 150px;
display: grid;
grid-template-columns: repeat(4, 4fr);
grid-gap: 40px;
justify-items: center;


    @media (max-width: 1060px) {
        grid-template-columns: repeat(2, 1fr);
        padding: 20px;
    }
    @media (max-width: 720px) {
        grid-template-columns: repeat(1, 1fr);
    }
`

const SectionCaption = styled.p`
  font-weight: 600;
  font-size: 18px;
  text-transform: uppercase;
  color: #94A4BA;
  text-align: center;
`
