import React from 'react'
import styled from 'styled-components'


import PlaceholderImage from "../images/logo-react.png";
import { LazyLoadImage } from 'react-lazy-load-image-component';
import 'react-lazy-load-image-component/src/effects/blur.css';

const Product = ({ product }) => {
    return (


        <CardContainer>
          <h1>Project Title</h1>
          <ProjectContainer>
          <a href={`/product/${product._id}`}>

                  <LazyLoadImage src={product.image} alt='alt' variant='top'
                        width={600} height={400}
                        PlaceholderSrc={PlaceholderImage}
                        effect="blur"
                    />
                </a>
          <h3>{product.title}</h3>
          </ProjectContainer>
          <p>CASE STUDY 02.03</p>
        </CardContainer>
    )
}

export default Product


const ProjectContainer = styled.div`
    margin-bottom: 100px;
    width: 1000px;
    height: 500px;
    position: relative;
    overflow: hidden;
    border-radius: 20px;
    box-shadow: 0 20px 40px black;
    display: grid;
    grid-template-rows: 1fr 1fr;
    transition: 0.8s cubic-bezier(0.2, 0.2, 0.165, 1);


    :hover { 
        transform: scale(1.1, 1.1);
        box-shadow: 0 30px 60px rgba(0,0,0, 0.5);
    }

    img {
        position: absolute;
        top: 0;
        height: 100%;
        width: 1000px;
        z-index: 1;
        transition: 0.8s cubic-bezier(0.2, 0.2, 0.165, 1);

        :hover {
            transform: translateY(-10px) scale(1.1, 1.1);;
        } 
    }

    h3 {
        color: white;
        font-size: 30px;
        margin: 20px 0 0 20px;
        width: 190px;
        z-index: 1;
    }

    p {
        color: white;
        font-size: 15px;
        margin: 20px 0 0 20px;
        width: 190px;
        z-index: 1;
        text-transform: uppercase;
        align-self: end;
        margin: 0 0 20px 20px;
    }
`

const CardContainer = styled.div`
    border-bottom: 1px solid rgba(0,0,0, 0.1);

`
