import { useState } from "react";
import '../styles/styles.css';
import styled from 'styled-components'

const Icon = ({ children }) => (
  <i className="material-symbols-outlined">{children}</i>
);

const Button = () => {

  const [isOpen, setIsOpen] = useState(false);

  return (
    <ButtonContainer className={`fab ${isOpen ? "open" : ""}`}>
    <Button onClick={() => setIsOpen(!isOpen)}>
      <Icon>add</Icon>
    </Button>
    <div className="menu">
      <Button>
        <Icon>GitHub</Icon>
        <span>GitHub</span>
      </Button>
      <Button>
        <Icon>Linkedin</Icon>
        <span>Documents</span>
      </Button>
      <Button>
        <Icon>Email</Icon>
        <span>Settings</span>
      </Button>
    </div>
  </ButtonContainer>
  )
}

export default Button


const ButtonContainer = styled.div`


