import React, {useState} from 'react'
import products from '../products'
import { Link } from 'react-router-dom'
import styled from 'styled-components'


const ProductScreen = (props) => {
    const product = products.find(p => p._id === props.match.params.id)
    const [modal, setModal] = useState(false);

    const toggleModal = () => {
        setModal(!modal);
      };

    return (
        <div>

            <Redirect>
                <Link className='btn btn-dark my-3' to='/'>
                    <img src='/images/right-arrow.png' alt='sdfg' /> 
                </Link>
            </Redirect>
            <ImageContainer>
                <h1>Project Overview</h1>
                <div>{product.title}</div>
                <img src={product.image} alt='product main' fluid />   

                <h2>Design Challenge</h2>
                <p>"Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum."</p>
                <h2>Solution</h2>
                <h3>Research Goals:</h3>
                <ul>
                <li>Identify the demographics of music streaming services</li>
                <li>Understand current market trends</li>
                <li>Determine Spotify’s competitors and their strengths/weaknesses</li>
                <li>Determine users’ frustrations/motivations/needs/goals</li>
                <li>Understand users’ experience with Spotify’s social capabilities</li>
                </ul>

                <h2>App Sitemap</h2>
                <img src={product.sitemap} alt='house img' fluid /> 
                <h3>Market research</h3>
                <p>"Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum."</p>
                <p>
                    I started off the project with research to understand and gain insight into the frustrations, and needs of the users. I summarized important points from gathered collection of data and statistics related to the research goals/topic for more insights:</p>


                <h3>{product.title}</h3>       
                <SideBySide>
                <div>
                <h1>Desktop</h1>
                <a onClick={toggleModal} className="btn-modal">
                <img src={product.sitemap} alt='house img' fluid />   
                </a>  


                {modal && (
        <div className="modal">
          <div onClick={toggleModal} className="overlay"></div>
          <div className="modal-content">
            <h2>Hello Modal</h2>
            <p>
              Lorem ipsum dolor sit amet consectetur adipisicing elit. Provident
              perferendis suscipit officia recusandae, eveniet quaerat assumenda
              id fugit, dignissimos maxime non natus placeat illo iusto!
              Sapiente dolorum id maiores dolores? Illum pariatur possimus
              quaerat ipsum quos molestiae rem aspernatur dicta tenetur. Sunt
              placeat tempora vitae enim incidunt porro fuga ea.
            </p>
            <button className="close-modal" onClick={toggleModal}>
              CLOSE
            </button>
          </div>
        </div>
      )}
                </div>
                <div>
                <h1>Mobile</h1>   
                <img src={product.sitemap} alt='house img' fluid />  
                </div>
                </SideBySide>

                <SideBySide>
                <div>
                <h1>Desktop</h1>
                <img src={product.sitemap} alt='house img' fluid />     
                </div>
                <div>
                <h1>Mobile</h1>   
                <img src={product.sitemap} alt='house img' fluid />  
                </div>
                </SideBySide>
            </ImageContainer>

            <Redirect>
                <Link className='btn btn-dark my-3' to='/'>
                    <img src='/images/right-arrow.png' alt='sdfg' /> 
                </Link>
            </Redirect>            
        </div>
    )
}

export default ProductScreen


const ImageContainer = styled.div`
    padding: 100px;
    display: grid;
    grid-template-columns: repeat(1, 1fr);
    grid-gap: 20px;
    justify-items: center;

    img {
        width: 1000px;
    }

        @media (max-width: 1060px) {
            grid-template-columns: repeat(2, 1fr);
            padding: 20px;
        }
        @media (max-width: 720px) {
            grid-template-columns: repeat(1, 1fr);
        }
`

const SideBySide = styled.div`
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    grid-gap: 40px;
    justify-items: center;
    text-align: center;

    img {
        width: 400px;
    }
`

const Redirect = styled.div`

    img {
        width: 50px;
        margin: 20px;
        transform: rotate(180deg);
        transition: 0.8s cubic-bezier(0.2, 0.2, 0.165, 1);
    }
`
